import React, { useState } from "react";
import axios from "axios";

const TextToSpeech = () => {
  const [text, setText] = useState("");
  const [audioUrl, setAudioUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post(
        "http://localhost:5000/text-to-speech",
        { message: text },
        {
          responseType: "blob", // Chúng ta cần nhận dữ liệu dưới dạng blob (binary data)
        }
      );

      // Tạo URL cho file audio MP3 từ blob
      const audioBlob = response.data;
      const audioUrl = URL.createObjectURL(audioBlob);
      setAudioUrl(audioUrl);
    } catch (err) {
      setError("Something went wrong. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Text to Speech</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={text}
          onChange={handleTextChange}
          rows="4"
          cols="50"
          placeholder="Enter text here"
        />
        <br />
        <button type="submit" disabled={loading}>
          {loading ? "Processing..." : "Generate Audio"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {audioUrl && (
        <div>
          <h2>Listen to the Generated Speech:</h2>
          <audio controls>
            <source src={audioUrl} type="audio/mpeg" />
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
};

export default TextToSpeech;
