import React, { useState } from "react";

const TextToImage = () => {
  const [imageUrl, setImageUrl] = useState(null);
  const [text, setText] = useState("");

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  return (
    <div className="tti-page">
      <h1 style={{ color: "#fafafa" }}>Chuyển văn bản thành hình ảnh</h1>

      <textarea
        className="text-area"
        value={text}
        onChange={handleTextChange}
        placeholder="Hãy nhập văn bản vào đây"
      />

      <button
        className="change-btn"
        type="button"
        disabled={loading}
        onClick={handleChangeToVoice}
      >
        {loading ? "Đang tạo..." : "Chuyển thành giọng nói"}
      </button>

      {error && <p className="error">{error}</p>}

      {audioUrl && (
        <div className="voice">
          <h2>Giọng nói đã được tạo:</h2>
          <audio controls>
            <source src={audioUrl} type="audio/mpeg" />
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
};

export default TextToImage;
